media_type.script:
var LineNotify = {
    token: null,
    message: null,

    sendMessage: function() {
        var params = {
            messages : [
                 {
                      type : 'text',
                      text : LineNotify.message
                 }
            ]
        },
        data,
        response,

        request = new CurlHttpRequest(),
        url = 'https://api.line.me/v2/bot/message/broadcast';

        request.AddHeader('Content-Type: application/json');
        request.AddHeader('Authorization: Bearer ' + LineNotify.token);
        data = JSON.stringify(params);

        // Remove replace() function if you want to see the exposed token in the log file.
        Zabbix.Log(4, '[LineNotify Webhook] URL: ' + url.replace(LineNotify.token, '<TOKEN>'));
        Zabbix.Log(4, '[LineNotify Webhook] params: ' + data);
        response = request.Post(url, data);
        Zabbix.Log(4, '[LineNotify Webhook] HTTP code: ' + request.Status());

        if (request.Status() !== 200 ) {
             throw 'Error : server reply status : ' + request.status(); 
        }
    }
}

try {
    var params = JSON.parse(value);

    if (typeof params.Token === 'undefined') {
        throw 'Incorrect value is given for parameter "Token": parameter is missing';
    }

    LineNotify.token = params.Token;

    LineNotify.message = params.Subject + '\n' + params.Message;
    LineNotify.sendMessage();

    return 'OK';
}
catch (error) {
    Zabbix.Log(4, '[LineNotify Webhook] notification failed: ' + error);
    throw 'Sending failed: ' + error + '.';
}

